mapper.select:
select esp.id, si.code, si.name, si.description, esp.default_result defaultResult from ent_survey_plan esp inner join survey_item si on esp.survey_item_id = si.id and si.valid = 1 and si.type = 'basic'
inner join survey_item_category sic on sic.survey_item_id = si.id and sic.valid = 1 and sic.category_code = #{param.categoryCode}
where esp.valid = 1 and esp.ent_id = #{param.enterpriseId} order by si.sort_num

select esp.id, si.code, si.name, si.description, esp.default_result defaultResult from ent_survey_plan esp inner join survey_item si on esp.survey_item_id = si.id and si.valid = 1 and si.type = 'basic'
inner join survey_item_category sic on sic.survey_item_id = si.id and sic.valid = 1 and sic.category_code = #{categoryCode}
where esp.valid = 1 and esp.ent_id = #{enterpriseId} order by si.sort_num

select esp.id, si.code, si.name, si.description, si.importance, si.cost, si.target_weight_code targetWeightCode, erar.assess_value assessValue, esp.default_result defaultResult, si.has_attachment hasAttachment from ent_survey_plan esp inner join survey_item si on esp.survey_item_id = si.id and si.valid = 1 and si.type = 'intensive'
inner join survey_item_category sic on sic.survey_item_id = si.id and sic.valid = 1 and sic.category_code = #{param.categoryCode}
left join ent_risk_assess_result erar on erar.id = esp.ent_target_result_id where esp.valid = 1 and esp.ent_id = #{param.enterpriseId} order by si.sort_num

select esp.id, si.code, si.name, si.description, si.importance, si.cost, si.target_weight_code targetWeightCode, erar.assess_value assessValue, esp.default_result defaultResult, si.has_attachment hasAttachment from ent_survey_plan esp inner join survey_item si on esp.survey_item_id = si.id and si.valid = 1 and si.type = 'intensive'
inner join survey_item_category sic on sic.survey_item_id = si.id and sic.valid = 1 and sic.category_code = #{categoryCode}
left join ent_risk_assess_result erar on erar.id = esp.ent_target_result_id where esp.valid = 1 and esp.ent_id = #{enterpriseId} order by si.sort_num

select esp.id, esp.default_result defaultResult, esp.excel_col_index excelColIndex, sic.category_code categoryCode from ent_survey_plan esp left join survey_item_category sic on (sic.survey_item_id=esp.survey_item_id and sic.valid=1) where esp.ent_id = #{enterpriseId}
and sic.category_code = #{categoryCode}
and esp.valid = 1 order by esp.excel_col_index
